import 'package:circular_clip_route/circular_clip_route.dart';import 'package:flutter/material.dart';import 'package:flutter_travel_app_ui/screens/home_screen.dart';import 'package:flutter_travel_app_ui/widgets/bottom_widget.dart';class SignInPage extends StatelessWidget { final _key = GlobalKey();  @override  Widget build(BuildContext context) {    return Scaffold(      body: Container(        height: MediaQuery.of(context).size.height,        width: MediaQuery.of(context).size.width,        decoration: BoxDecoration(          image: DecorationImage(            image: AssetImage('assets/image.jpg'),            fit: BoxFit.cover          ),        ),        child: Stack(          children: [            Column(              mainAxisAlignment: MainAxisAlignment.center,              children: [                SizedBox(height: 50,),                Text('Escape the \nOrdinary Life',style: TextStyle(                    fontSize: 40,fontWeight: FontWeight.w900,color: Colors.black                ),textAlign: TextAlign.center,),                Padding(                  padding: const EdgeInsets.symmetric(horizontal: 20,vertical: 5),                  child: Text('Travel and be inspiredby the hottest spots in the world.',style: TextStyle(                      fontSize: 20,fontWeight: FontWeight.w700,color: Colors.black                  ),textAlign: TextAlign.center,),                ),                Container(                  margin: EdgeInsets.fromLTRB(25, 22, 25, 5),                  decoration: BoxDecoration(                    borderRadius: BorderRadius.circular(5),                    color: Colors.deepOrange,                  ),                  height: 60,                  child: Center(                    child: InkWell(                        onTap: (){                          Navigator.push(context, CircularClipRoute(                              expandFrom: _key.currentContext,                               builder: (context) => HomePage(),                            curve: Curves.fastOutSlowIn,                            opacity: ConstantTween(1),                            transitionDuration: Duration(seconds: 1),                          ));                        },                        child: Text('GET STARTED',style: TextStyle(                        color: Colors.white,fontSize: 20,fontWeight: FontWeight.w500                    ),)),                  ),                ),                Container(                  margin: EdgeInsets.fromLTRB(25, 10, 25, 5),                  decoration: BoxDecoration(                    borderRadius: BorderRadius.circular(5),                    color: Colors.deepOrange.shade100,                  ),                  height: 60,                  child: Center(                    child: Text('SIGN IN',style: TextStyle(                        color: Colors.deepOrange,fontSize: 20,fontWeight: FontWeight.w500                    ),),                  ),                ),              ],            ),            Positioned(              left: 20,                right: 20,                bottom: 50,                child: Column(                  mainAxisAlignment: MainAxisAlignment.center,                  children: [                    Text('Join 500K users today',style: TextStyle(                      color: Colors.white,fontWeight: FontWeight.w500                    ),),                  ],                ),            ),            BottomWidget(              left: 0,            ),            BottomWidget(              left: 20,            ),            BottomWidget(              left: 40,            ),            BottomWidget(              key: _key,              left: 60,            ),          ],        )      ),    );  }}