import 'package:flutter/material.dart';class SelectWidget extends StatelessWidget {  final Color backgroundColors;  final Color textColors;  final String text;  const SelectWidget({Key key, this.backgroundColors, this.textColors, this.text}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      decoration: BoxDecoration(          color: backgroundColors,          borderRadius: BorderRadius.circular(10)      ),      height: 30,      child: Center(        child: Padding(          padding: const EdgeInsets.symmetric(horizontal: 20),          child: Text(text,style: TextStyle(              color: textColors,fontWeight: FontWeight.w500          ),),        ),      ),    );  }}